import React, { useEffect, useState, useRef } from "react";
import {
  SpeacilDealsContainer,
  SpeacilDealsBox,
  TimerBox,
  TimerCount,
  ItemBox,
  ItemImageBox,
  ItemSticker,
  ItemTextBox,
  ItemFooter,
  EventPriceBox,
} from "./styles";
import { CartButton } from "../Suggest/SuggestList";

const SpeacilDeals = ({ children }) => {
  const [hour, setHour] = useState(23 - new Date().getHours());
  const [minute, setMinute] = useState(59 - new Date().getMinutes());
  const [second, SetSecond] = useState(59 - new Date().getSeconds());

  useEffect(() => {
    const id = setInterval(() => {
      setHour(23 - new Date().getHours());
      setMinute(59 - new Date().getMinutes());
      SetSecond(59 - new Date().getSeconds());
    }, 1000);
    return () => clearInterval(id);
  }, []);

  return (
    <>
      <SpeacilDealsContainer>
        <SpeacilDealsBox>
          <TimerBox>
            <h2>{children}</h2>
            <h3>24시간 한정 특가</h3>

            <TimerCount>
              <span> {hour < 10 ? "0" + hour : hour}:</span>
              <span> {minute < 10 ? "0" + minute : minute}:</span>
              <span> {second < 10 ? "0" + second : second}</span>
            </TimerCount>
            <p>망설이면 늦어요!</p>
          </TimerBox>
          <ItemBox>
            <div>
              <ItemImageBox>
                <img
                  src="https://img-cf.kurly.com/shop/data/goods/163177531664z0.jpg"
                  alt="상품 이미지"
                />
                <ItemSticker>
                  <span>추석특가</span>
                </ItemSticker>
                <CartButton>
                  <img
                    src="data:image/svg+xml;base64,"
                    alt="장바구니 아이콘"
                  />
                </CartButton>
              </ItemImageBox>
              <ItemTextBox>
                <div>하루 한 알로 섭취하는 오메가3</div>
                <h3>솔가 오메가-3 700 (60일분)</h3>
                <ItemFooter>
                  <EventPriceBox>
                    <span>57%</span>
                    <span>19,780원</span>
                    <span>46,000원</span>
                  </EventPriceBox>
                </ItemFooter>
              </ItemTextBox>
            </div>
          </ItemBox>
        </SpeacilDealsBox>
      </SpeacilDealsContainer>
    </>
  );
};

export default SpeacilDeals;
